#from visual import *
""" Simple bouncing ball demo."""
from __future__ import print_function, division
import visual as vp

print(__doc__)

FLOOR = vp.box(length=4, height=0.5, width=4, color=vp.color.blue)

BALL = vp.sphere(pos=(0, 4, 0), color=vp.color.red)
BALL.velocity = vp.vector(0, -1, 0)

DT = 0.01
while 1:
    vp.rate(100)
    BALL.pos = BALL.pos + BALL.velocity*DT
    if BALL.y < 1:
        BALL.velocity.y = -BALL.velocity.y
    else:
        BALL.velocity.y = BALL.velocity.y - 9.8*DT
